import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaSearch, FaMapMarkerAlt, FaStethoscope, FaMoneyBillWave, FaUser, FaHeartbeat, FaWalking, FaBaby, FaBrain, FaEye, FaTooth, FaHandHoldingMedical, FaProcedures, FaHospital, FaFlask, FaVial, FaNotesMedical, FaCalendarAlt, FaPhoneAlt } from 'react-icons/fa';

const Home = () => {
  const [searchParams, setSearchParams] = useState({
    symptoms: '',
    location: '',
    specialty: '',
    doctorName: ''
  });
  const [doctors, setDoctors] = useState([]);
  const [featuredDoctors, setFeaturedDoctors] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const [bookingForm, setBookingForm] = useState({
    name: '',
    phone: '',
    email: '',
    date: '',
    time: '',
    service: '',
    message: '',
    serviceType: '',
    collectionLocation: '',
  });

  const [physioBookingForm, setPhysioBookingForm] = useState({
    name: '',
    phone: '',
    email: '',
    date: '',
    time: '',
    service: '',
    message: '',
    serviceType: 'Physiotherapy',
    collectionLocation: '',
  });

  const [mentalBookingForm, setMentalBookingForm] = useState({
    name: '',
    phone: '',
    email: '',
    date: '',
    time: '',
    service: '',
    message: '',
    serviceType: 'Mental Health',
    collectionLocation: '',
  });

  const [bloodBookingForm, setBloodBookingForm] = useState({
    name: '',
    phone: '',
    email: '',
    date: '',
    time: '',
    service: '',
    message: '',
    serviceType: 'Blood Collection',
    collectionLocation: '',
  });

  const [bookingSuccess, setBookingSuccess] = useState(false);
  const [bookingError, setBookingError] = useState(null);

  const handleSearchInputChange = (e) => {
    const { name, value } = e.target;
    setSearchParams((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleDoctorSearch = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    try {
      const response = await axios.get('http://localhost:5001/api/doctors/search', {
        params: searchParams,
      });
      setDoctors(response.data);
    } catch (err) {
      setError('Failed to fetch doctors');
    } finally {
      setLoading(false);
    }
  };

  const handleBookingInputChange = (e) => {
    const { name, value } = e.target;
    setBookingForm((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handlePhysioBookingInputChange = (e) => {
    const { name, value } = e.target;
    setPhysioBookingForm((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleMentalBookingInputChange = (e) => {
    const { name, value } = e.target;
    setMentalBookingForm((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleBloodBookingInputChange = (e) => {
    const { name, value } = e.target;
    setBloodBookingForm((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleBookingSubmit = async (e) => {
    e.preventDefault();

    let currentBookingForm = {};
    let currentServiceType = '';

    // Determine which form is being submitted based on the event target or a hidden input
    // For now, we'll assume the original bookingForm is for physiotherapy
    // This logic will need to be refined once we differentiate the forms in JSX
    if (e.target.id === 'physio-form') {
      currentBookingForm = physioBookingForm;
      currentServiceType = 'Physiotherapy';
    } else if (e.target.id === 'mental-form') {
      currentBookingForm = mentalBookingForm;
      currentServiceType = 'Mental Health';
    } else if (e.target.id === 'blood-form') {
      currentBookingForm = bloodBookingForm;
      currentServiceType = 'Blood Collection';
    } else {
      // Fallback or error handling if form ID is not found
      setBookingError('Unknown form submission.');
      return;
    }

    console.log('Form submitted:', e.target.id, currentBookingForm, currentServiceType);

    const bookingData = {
      ...currentBookingForm,
      serviceType: currentServiceType,
    };

    // ... existing code ...
    // End of handleBookingSubmit logic
    // ... existing code ...
    // The function should end here
  }

  return (
    <div>
      {/* Doctor Search Bar */}
      <form onSubmit={handleDoctorSearch} className="bg-white p-6 rounded-2xl shadow-xl border-t-4 border-blue-500 mb-8">
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-4">
          <div className="flex items-center border rounded-lg px-3">
            <FaSearch className="text-gray-400 mr-2" />
            <input
              type="text"
              name="symptoms"
              value={searchParams.symptoms}
              onChange={handleSearchInputChange}
              className="w-full py-2 outline-none"
              placeholder="Search by symptoms"
            />
          </div>
          <div className="flex items-center border rounded-lg px-3">
            <FaMapMarkerAlt className="text-gray-400 mr-2" />
            <input
              type="text"
              name="location"
              value={searchParams.location}
              onChange={handleSearchInputChange}
              className="w-full py-2 outline-none"
              placeholder="Location"
            />
          </div>
          <div className="flex items-center border rounded-lg px-3">
            <FaStethoscope className="text-gray-400 mr-2" />
            <input
              type="text"
              name="specialty"
              value={searchParams.specialty}
              onChange={handleSearchInputChange}
              className="w-full py-2 outline-none"
              placeholder="Specialty"
            />
          </div>
          <div className="flex items-center border rounded-lg px-3">
            <FaUser className="text-gray-400 mr-2" />
            <input
              type="text"
              name="doctorName"
              value={searchParams.doctorName}
              onChange={handleSearchInputChange}
              className="w-full py-2 outline-none"
              placeholder="Doctor Name"
            />
          </div>
        </div>
        <button
          type="submit"
          className="w-full md:w-auto bg-blue-600 text-white py-2 px-6 rounded-lg font-semibold hover:bg-blue-700 transition"
        >
          Search Doctors
        </button>
      </form>

      {/* Doctor List */}
      {loading ? (
        <div className="text-center text-lg">Loading...</div>
      ) : error ? (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">{error}</div>
      ) : doctors.length > 0 ? (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8 mb-12">
          {doctors.map((doctor) => (
            <div key={doctor._id} className="bg-white rounded-2xl shadow-lg p-6 flex flex-col justify-between border-t-4 border-blue-500 hover:shadow-2xl transition">
              <div>
                <h2 className="text-xl font-bold text-blue-700 flex items-center gap-2 mb-1"><FaUser /> {doctor.name}</h2>
                <span className="inline-block bg-blue-100 text-blue-700 text-xs px-2 py-1 rounded-full mb-2">{doctor.specialty}</span>
                <p className="text-gray-600 mb-1 flex items-center"><FaHospital className="mr-1" /> {doctor.hospitalName}</p>
                <p className="text-gray-500 mb-1">{doctor.address}, {doctor.city}, {doctor.state}</p>
                <p className="text-gray-500 mb-1 flex items-center"><FaPhoneAlt className="mr-1" /> {doctor.phone}</p>
                <p className="text-gray-500 mb-1 flex items-center"><FaMoneyBillWave className="mr-1" /> Fees: <span className="font-semibold ml-1">{doctor.fees}</span></p>
                <p className="text-gray-500 mb-1 flex items-center"><FaCalendarAlt className="mr-1" /> {doctor.availability}</p>
                <div className="mt-2 flex flex-wrap gap-2">
                  {doctor.symptomsTreated && doctor.symptomsTreated.map((sym, idx) => (
                    <span key={idx} className="bg-gray-200 text-gray-700 text-xs px-2 py-1 rounded-full">{sym}</span>
                  ))}
                </div>
              </div>
            </div>
          ))}
        </div>
      ) : null}

      {/* Physiotherapy Services */}
      <div className="mb-12">
        <div className="flex items-center mb-6">
          <div className="w-10 h-1 bg-green-600 rounded-full mr-4"></div>
          <h2 className="text-3xl font-bold text-green-800">Physiotherapy Services</h2>
        </div>
        <div className="bg-white p-8 rounded-2xl shadow-xl border-t-4 border-green-500">
