import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaSearch, FaMapMarkerAlt, FaStethoscope, FaMoneyBillWave, FaUser } from 'react-icons/fa';

const Home = () => {
  const [searchParams, setSearchParams] = useState({
    symptoms: '',
    location: '',
    specialty: '',
    doctorName: ''
  });
  const [doctors, setDoctors] = useState([]);
  const [featuredDoctors, setFeaturedDoctors] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    // Fetch featured doctors
    const fetchFeaturedDoctors = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/doctors/featured');
        setFeaturedDoctors(res.data);
      } catch (err) {
        // handle error
      }
    };

    // Fetch all doctors
    const fetchAllDoctors = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/doctors');
        setDoctors(res.data);
      } catch (err) {
        // handle error
      }
    };

    fetchFeaturedDoctors();
    fetchAllDoctors();
  }, []);

  const handleSearch = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const params = new URLSearchParams();
      Object.entries(searchParams).forEach(([key, value]) => {
        if (value) params.append(key, value);
      });

      const response = await axios.get(`http://localhost:5000/api/doctors/search?${params}`);
      setDoctors(response.data);
    } catch (err) {
      setError('Failed to fetch doctors. Please try again.');
      console.error('Search error:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setSearchParams(prev => ({
      ...prev,
      [name]: value
    }));
  };

  return (
    <div className="max-w-5xl mx-auto">
      {/* Hero Section */}
      <div className="bg-gradient-to-r from-blue-600 to-blue-400 rounded-xl shadow-lg p-10 mb-10 flex flex-col items-center justify-center text-white relative overflow-hidden">
        <div className="absolute right-0 top-0 opacity-20 text-[10rem] pointer-events-none select-none">
          <FaStethoscope />
        </div>
        <h1 className="text-4xl md:text-5xl font-extrabold mb-2 z-10">Find the Right Doctor</h1>
        <p className="text-lg md:text-xl mb-4 z-10">Search by symptoms, location, specialty, and more</p>
      </div>

      {/* Featured Doctors */}
      {featuredDoctors.length > 0 && (
        <div className="mb-10">
          <h2 className="text-2xl font-bold mb-4 text-blue-700">Featured Doctors</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
            {featuredDoctors.map(doctor => (
              <div key={doctor._id} className="bg-white p-6 rounded-2xl shadow-xl hover:shadow-2xl transition border-t-4 border-yellow-500 flex flex-col justify-between h-full">
                <div>
                  <h2 className="text-2xl font-bold mb-1 text-yellow-700 flex items-center gap-2"><FaStethoscope className="inline-block" /> {doctor.name}</h2>
                  <span className="inline-block bg-yellow-100 text-yellow-700 text-xs px-2 py-1 rounded-full mb-2">{doctor.specialty}</span>
                  <p className="text-gray-600 mb-2 font-medium">{doctor.hospitalName}</p>
                  <p className="text-gray-500 mb-2 flex items-center"><FaMapMarkerAlt className="mr-1" /> {doctor.address}, {doctor.city}, {doctor.state}</p>
                  <p className="text-gray-500 mb-2">Phone: <span className="font-semibold">{doctor.phone}</span></p>
                  <p className="text-gray-500 mb-2">Fees: <span className="font-semibold">₹{doctor.fees}</span></p>
                  <p className="text-gray-500 mb-2">Availability: <span className="font-semibold">{doctor.availability}</span></p>
                </div>
                <div className="mt-4 flex flex-wrap gap-2">
                  {doctor.symptomsTreated && doctor.symptomsTreated.map((sym, idx) => (
                    <span key={idx} className="bg-gray-200 text-gray-700 text-xs px-2 py-1 rounded-full">{sym}</span>
                  ))}
                </div>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Search Form */}
      <form onSubmit={handleSearch} className="bg-white p-8 rounded-lg shadow-md mb-10">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <div className="relative">
            <label className="block text-sm font-medium text-gray-700 mb-1">Symptoms</label>
            <span className="absolute left-3 top-9 text-gray-400"><FaSearch /></span>
            <input
              type="text"
              name="symptoms"
              value={searchParams.symptoms}
              onChange={handleInputChange}
              className="pl-10 mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
              placeholder="e.g. fever, cough"
            />
          </div>
          <div className="relative">
            <label className="block text-sm font-medium text-gray-700 mb-1">Location</label>
            <span className="absolute left-3 top-9 text-gray-400"><FaMapMarkerAlt /></span>
            <input
              type="text"
              name="location"
              value={searchParams.location}
              onChange={handleInputChange}
              className="pl-10 mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
              placeholder="City or State"
            />
          </div>
          <div className="relative">
            <label className="block text-sm font-medium text-gray-700 mb-1">Specialty</label>
            <span className="absolute left-3 top-9 text-gray-400"><FaStethoscope /></span>
            <input
              type="text"
              name="specialty"
              value={searchParams.specialty}
              onChange={handleInputChange}
              className="pl-10 mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
              placeholder="e.g. Cardiologist"
            />
          </div>
          <div className="relative">
            <label className="block text-sm font-medium text-gray-700 mb-1">Doctor Name</label>
            <span className="absolute left-3 top-9 text-gray-400"><FaUser /></span>
            <input
              type="text"
              name="doctorName"
              value={searchParams.doctorName}
              onChange={handleInputChange}
              className="pl-10 mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
              placeholder="e.g. Sujay Kumar Das"
            />
          </div>
        </div>
        <div className="mt-8">
          <button
            type="submit"
            disabled={loading}
            className="w-full bg-blue-600 text-white py-3 px-4 rounded-md text-lg font-semibold hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 disabled:opacity-50 transition"
          >
            {loading ? (
              <span className="flex items-center justify-center"><svg className="animate-spin h-5 w-5 mr-2 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24"><circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle><path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8v8z"></path></svg>Searching...</span>
            ) : 'Search Doctors'}
          </button>
        </div>
      </form>

      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          {error}
        </div>
      )}

      {/* Doctor Results */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
        {doctors.map(doctor => (
          <div key={doctor._id} className="bg-white p-6 rounded-2xl shadow-xl hover:shadow-2xl transition border-t-4 border-blue-500 flex flex-col justify-between h-full">
            <div>
              <h2 className="text-2xl font-bold mb-1 text-blue-700 flex items-center gap-2"><FaStethoscope className="inline-block" /> {doctor.name}</h2>
              <span className="inline-block bg-blue-100 text-blue-700 text-xs px-2 py-1 rounded-full mb-2">{doctor.specialty}</span>
              <p className="text-gray-600 mb-2 font-medium">{doctor.hospitalName}</p>
              <p className="text-gray-500 mb-2 flex items-center"><FaMapMarkerAlt className="mr-1" /> {doctor.address}, {doctor.city}, {doctor.state}</p>
              <p className="text-gray-500 mb-2">Phone: <span className="font-semibold">{doctor.phone}</span></p>
              <p className="text-gray-500 mb-2">Fees: <span className="font-semibold">₹{doctor.fees}</span></p>
              <p className="text-gray-500 mb-2">Availability: <span className="font-semibold">{doctor.availability}</span></p>
            </div>
            <div className="mt-4 flex flex-wrap gap-2">
              {doctor.symptomsTreated && doctor.symptomsTreated.map((sym, idx) => (
                <span key={idx} className="bg-gray-200 text-gray-700 text-xs px-2 py-1 rounded-full">{sym}</span>
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
