import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaSearch, FaMapMarkerAlt, FaStethoscope, FaMoneyBillWave, FaUser, FaHeartbeat, FaWalking, FaBaby, FaBrain, FaEye, FaTooth, FaHandHoldingMedical, FaProcedures, FaHospital, FaFlask, FaVial, FaNotesMedical, FaCalendarAlt, FaPhoneAlt } from 'react-icons/fa';

const Home = () => {
  const [searchParams, setSearchParams] = useState({
    symptoms: '',
    location: '',
    specialty: '',
    doctorName: ''
  });
  const [doctors, setDoctors] = useState([]);
  const [featuredDoctors, setFeaturedDoctors] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const [showSuggestions, setShowSuggestions] = useState(false);
  const [bookingForm, setBookingForm] = useState({
    name: '',
    phone: '',
    email: '',
    date: '',
    time: '',
    service: '',
    message: ''
  });
  const [bookingSuccess, setBookingSuccess] = useState(false);
  const [bookingError, setBookingError] = useState('');

  // Define common specialties with icons
  const specialties = [
    { name: 'Cardiologist', icon: <FaHeartbeat />, color: 'red' },
    { name: 'Orthopedic', icon: <FaWalking />, color: 'green' },
    { name: 'Gynecologist', icon: <FaBaby />, color: 'pink' },
    { name: 'Neurologist', icon: <FaBrain />, color: 'purple' },
    { name: 'Ophthalmologist', icon: <FaEye />, color: 'blue' },
    { name: 'Dentist', icon: <FaTooth />, color: 'yellow' },
  ];

  useEffect(() => {
    // Fetch featured doctors
    const fetchFeaturedDoctors = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/doctors/featured');
        setFeaturedDoctors(res.data);
      } catch (err) {
        // handle error
      }
    };

    // Fetch all doctors
    const fetchAllDoctors = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/doctors');
        setDoctors(res.data);
      } catch (err) {
        // handle error
      }
    };

    fetchFeaturedDoctors();
    fetchAllDoctors();
  }, []);

  // Combine all search fields for suggestion query
  const getCombinedQuery = () => {
    return Object.values(searchParams).filter(Boolean).join(' ');
  };

  // Fetch suggestions as user types
  useEffect(() => {
    const fetchSuggestions = async () => {
      const q = getCombinedQuery();
      if (!q) {
        setSuggestions([]);
        setShowSuggestions(false);
        return;
      }
      try {
        const res = await axios.get(`http://localhost:5000/api/doctors/suggest?q=${encodeURIComponent(q)}`);
        setSuggestions(res.data);
        setShowSuggestions(true);
      } catch (err) {
        setSuggestions([]);
        setShowSuggestions(false);
      }
    };
    fetchSuggestions();
    // eslint-disable-next-line
  }, [searchParams.symptoms, searchParams.location, searchParams.specialty, searchParams.doctorName]);

  const handleSuggestionClick = (suggestion) => {
    setSearchParams(prev => ({
      ...prev,
      doctorName: suggestion.name,
      location: suggestion.city || '',
      specialty: suggestion.specialty || ''
    }));
    setShowSuggestions(false);
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const params = new URLSearchParams();
      Object.entries(searchParams).forEach(([key, value]) => {
        if (value) params.append(key, value);
      });

      const response = await axios.get(`http://localhost:5000/api/doctors/search?${params}`);
      setDoctors(response.data);
    } catch (err) {
      setError('Failed to fetch doctors. Please try again.');
      console.error('Search error:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setSearchParams(prev => ({
      ...prev,
      [name]: value
    }));
    setShowSuggestions(true);
  };

  // Handle specialty card click
  const handleSpecialtyClick = (specialty) => {
    setSearchParams(prev => ({
      ...prev,
      specialty: specialty
    }));
    
    // Automatically search with the selected specialty
    const searchWithSpecialty = async () => {
      setLoading(true);
      setError('');
      
      try {
        const params = new URLSearchParams();
        params.append('specialty', specialty);
        
        const response = await axios.get(`http://localhost:5000/api/doctors/search?${params}`);
        setDoctors(response.data);
      } catch (err) {
        setError('Failed to fetch doctors. Please try again.');
        console.error('Search error:', err);
      } finally {
        setLoading(false);
      }
    };
    
    searchWithSpecialty();
  };

  // Handle booking form input changes
  const handleBookingInputChange = (e) => {
    const { name, value } = e.target;
    setBookingForm(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // Handle booking form submission
  const handleBookingSubmit = (e) => {
    e.preventDefault();
    setBookingSuccess(true);
    setBookingError('');
    // Reset form after successful submission
    setTimeout(() => {
      setBookingForm({
        name: '',
        phone: '',
        email: '',
        date: '',
        time: '',
        service: '',
        message: ''
      });
      setBookingSuccess(false);
    }, 3000);
    // In a real application, you would send this data to your backend
    // axios.post('http://localhost:5000/api/bookings', bookingForm);
  };

  return (
    <div className="relative min-h-screen">
      {/* Background Image */}
      <div className="fixed inset-0 z-0 pointer-events-none">
        <img src="/medical-bg.svg" alt="" className="w-full h-full object-cover" />
      </div>
      
      <div className="max-w-5xl mx-auto relative z-10 px-4 py-8">
        {/* Hero Section */}
        <div className="bg-gradient-to-r from-blue-700 to-blue-500 rounded-2xl shadow-2xl p-12 mb-12 flex flex-col items-center justify-center text-white relative overflow-hidden backdrop-blur-sm bg-opacity-90">
          <div className="absolute -right-10 -top-10 opacity-20 text-[15rem] pointer-events-none select-none">
            <FaStethoscope />
          </div>
          <h1 className="text-4xl md:text-6xl font-extrabold mb-4 z-10 text-center">Find the Right Doctor</h1>
          <p className="text-lg md:text-2xl mb-6 z-10 text-center max-w-2xl">Your health is our priority. Search by symptoms, location, specialty, and more</p>
          <div className="w-24 h-1 bg-white rounded-full opacity-50 mb-6"></div>
          <p className="text-blue-100 italic">Trusted by thousands of patients</p>
        </div>

        {/* Specialty Cards Section */}
        <div className="mb-12">
          <div className="flex items-center mb-6">
            <div className="w-10 h-1 bg-blue-600 rounded-full mr-4"></div>
            <h2 className="text-3xl font-bold text-blue-800">Browse by Specialty</h2>
          </div>
          <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-6 gap-4">
            {specialties.map((specialty, index) => (
              <div 
                key={index} 
                onClick={() => handleSpecialtyClick(specialty.name)}
                className={`bg-white p-6 rounded-2xl shadow-lg hover:shadow-xl transition cursor-pointer flex flex-col items-center justify-center text-center transform hover:-translate-y-1 duration-300 border-t-4 border-${specialty.color}-500 h-full`}
              >
                <div className={`text-${specialty.color}-500 text-4xl mb-3`}>
                  {specialty.icon}
                </div>
                <h3 className="font-bold text-gray-800">{specialty.name}</h3>
              </div>
            ))}
          </div>
        </div>

        {/* Featured Doctors */}
        {featuredDoctors.length > 0 && (
          <div className="mb-12">
            <div className="flex items-center mb-6">
              <div className="w-10 h-1 bg-blue-600 rounded-full mr-4"></div>
              <h2 className="text-3xl font-bold text-blue-800">Featured Doctors</h2>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
              {featuredDoctors.map(doctor => (
                <div key={doctor._id} className="bg-white p-6 rounded-2xl shadow-xl hover:shadow-2xl transition border-t-4 border-yellow-500 flex flex-col justify-between h-full transform hover:-translate-y-1 duration-300">
                  <div>
                    <h2 className="text-2xl font-bold mb-1 text-yellow-700 flex items-center gap-2"><FaStethoscope className="inline-block" /> {doctor.name}</h2>
                    <span className="inline-block bg-yellow-100 text-yellow-700 text-xs px-2 py-1 rounded-full mb-2">{doctor.specialty}</span>
                    <p className="text-gray-600 mb-2 font-medium">{doctor.hospitalName}</p>
                    <p className="text-gray-500 mb-2 flex items-center"><FaMapMarkerAlt className="mr-1" /> {doctor.address}, {doctor.city}, {doctor.state}</p>
                    <p className="text-gray-500 mb-2">Phone: <span className="font-semibold">{doctor.phone}</span></p>
                    <p className="text-gray-500 mb-2">Fees: <span className="font-semibold">{doctor.fees}</span></p>
                    <p className="text-gray-500 mb-2">Availability: <span className="font-semibold">{doctor.availability}</span></p>
                  </div>
                  <div className="mt-4 flex flex-wrap gap-2">
                    {doctor.symptomsTreated && doctor.symptomsTreated.map((sym, idx) => (
                      <span key={idx} className="bg-gray-200 text-gray-700 text-xs px-2 py-1 rounded-full">{sym}</span>
                    ))}
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Search Form */}
        <form onSubmit={handleSearch} className="bg-white p-8 rounded-2xl shadow-2xl mb-12 transform transition-all duration-300 hover:shadow-2xl border border-blue-100 backdrop-filter backdrop-blur-sm bg-opacity-90">
          <div className="flex items-center mb-6">
            <div className="w-8 h-1 bg-blue-600 rounded-full mr-4"></div>
            <h2 className="text-2xl font-bold text-blue-800">Find Your Doctor</h2>
          </div>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div className="relative group">
              <label className="block text-sm font-medium text-gray-700 mb-1">Symptoms</label>
              <span className="absolute left-3 top-9 text-blue-500 group-hover:text-blue-700 transition-colors"><FaSearch /></span>
              <input
                type="text"
                name="symptoms"
                value={searchParams.symptoms}
                onChange={handleInputChange}
                className="pl-10 mt-1 block w-full rounded-lg border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500 transition-all duration-300 hover:border-blue-300"
                placeholder="e.g. fever, cough"
              />
            </div>
            <div className="relative group">
              <label className="block text-sm font-medium text-gray-700 mb-1">Location</label>
              <span className="absolute left-3 top-9 text-blue-500 group-hover:text-blue-700 transition-colors"><FaMapMarkerAlt /></span>
              <input
                type="text"
                name="location"
                value={searchParams.location}
                onChange={handleInputChange}
                className="pl-10 mt-1 block w-full rounded-lg border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500 transition-all duration-300 hover:border-blue-300"
                placeholder="City or State"
              />
            </div>
            <div className="relative group">
              <label className="block text-sm font-medium text-gray-700 mb-1">Specialty</label>
              <span className="absolute left-3 top-9 text-blue-500 group-hover:text-blue-700 transition-colors"><FaStethoscope /></span>
              <input
                type="text"
                name="specialty"
                value={searchParams.specialty}
                onChange={handleInputChange}
                className="pl-10 mt-1 block w-full rounded-lg border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500 transition-all duration-300 hover:border-blue-300"
                placeholder="e.g. Cardiologist"
              />
            </div>
            <div className="relative group">
              <label className="block text-sm font-medium text-gray-700 mb-1">Doctor Name</label>
              <span className="absolute left-3 top-9 text-blue-500 group-hover:text-blue-700 transition-colors"><FaUser /></span>
              <input
                type="text"
                name="doctorName"
                value={searchParams.doctorName}
                onChange={handleInputChange}
                className="pl-10 mt-1 block w-full rounded-lg border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500 transition-all duration-300 hover:border-blue-300"
                placeholder="e.g. Sujay Kumar Das"
              />
            </div>
          </div>
          {/* Suggestions dropdown */}
          {showSuggestions && suggestions.length > 0 && (
            <div className="bg-white border rounded-lg shadow-lg mt-2 max-h-60 overflow-y-auto absolute z-50 w-full md:w-auto md:min-w-[300px] animate-fadeIn">
              {suggestions.map((s, idx) => (
                <div
                  key={s._id || idx}
                  className="px-4 py-2 hover:bg-blue-50 cursor-pointer transition-colors duration-200"
                  onClick={() => handleSuggestionClick(s)}
                >
                  <div className="font-semibold text-blue-700">{s.name}</div>
                  <div className="text-xs text-gray-500">{s.specialty} | {s.city} {s.state} {s.hospitalName}</div>
                </div>
              ))}
            </div>
          )}
          <div className="mt-8">
            <button
              type="submit"
              disabled={loading}
              className="w-full bg-gradient-to-r from-blue-700 to-blue-500 text-white py-4 px-6 rounded-lg text-lg font-semibold hover:from-blue-800 hover:to-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 disabled:opacity-50 transition-all duration-300 transform hover:scale-[1.01] shadow-lg hover:shadow-xl"
            >
              {loading ? (
                <span className="flex items-center justify-center"><svg className="animate-spin h-5 w-5 mr-2 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24"><circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle><path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8v8z"></path></svg>Searching...</span>
              ) : (
                <span className="flex items-center justify-center"><FaSearch className="mr-2" /> Search Doctors</span>
              )}
            </button>
          </div>
        </form>

        {error && (
          <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
            {error}
          </div>
        )}

        {/* Physiotherapy Booking Section */}
        <div className="mb-12">
          <div className="flex items-center mb-6">
            <div className="w-10 h-1 bg-green-600 rounded-full mr-4"></div>
            <h2 className="text-3xl font-bold text-green-800">Physiotherapy Services</h2>
          </div>
          <div className="bg-white p-8 rounded-2xl shadow-xl border-t-4 border-green-500">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
              <div>
                <div className="text-green-500 text-5xl mb-4">
                  <FaHandHoldingMedical />
                </div>
                <h3 className="text-2xl font-bold text-gray-800 mb-4">Professional Physiotherapy</h3>
                <p className="text-gray-600 mb-4">Our expert physiotherapists provide personalized treatment plans to help you recover from injuries, manage chronic pain, and improve mobility.</p>
                <ul className="space-y-2 mb-6">
                  <li className="flex items-center"><span className="bg-green-100 p-1 rounded-full mr-2"></span> Sports Injury Rehabilitation</li>
                  <li className="flex items-center"><span className="bg-green-100 p-1 rounded-full mr-2"></span> Post-Surgery Recovery</li>
                  <li className="flex items-center"><span className="bg-green-100 p-1 rounded-full mr-2"></span> Chronic Pain Management</li>
                  <li className="flex items-center"><span className="bg-green-100 p-1 rounded-full mr-2"></span> Neurological Rehabilitation</li>
                </ul>
                <div className="flex items-center text-gray-600 mb-4">
                  <FaPhoneAlt className="mr-2 text-green-500" />
                  <span>Call us: <span className="font-semibold">+91 9876543210</span></span>
                </div>
              </div>
              <div>
                <form onSubmit={handleBookingSubmit} className="bg-gray-50 p-6 rounded-xl">
                  <h3 className="text-xl font-bold text-gray-800 mb-4">Book Your Physiotherapy Session</h3>
                  
                  {bookingSuccess && (
                    <div className="bg-green-100 border border-green-400 text-green-700 px-4 py-3 rounded mb-4">
                      Booking request submitted successfully! We'll contact you shortly.
                    </div>
                  )}
                  
                  {bookingError && (
                    <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
                      {bookingError}
                    </div>
                  )}
                  
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">Your Name</label>
                      <input
                        type="text"
                        name="name"
                        value={bookingForm.name}
                        onChange={handleBookingInputChange}
                        className="w-full rounded-lg border-gray-300 shadow-sm focus:border-green-500 focus:ring-green-500"
                        required
                      />
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">Phone Number</label>
                      <input
                        type="tel"
                        name="phone"
                        value={bookingForm.phone}
                        onChange={handleBookingInputChange}
                        className="w-full rounded-lg border-gray-300 shadow-sm focus:border-green-500 focus:ring-green-500"
                        required
                      />
                    </div>
                  </div>
                  
                  <div className="mb-4">
                    <label className="block text-sm font-medium text-gray-700 mb-1">Email Address</label>
                    <input
                      type="email"
                      name="email"
                      value={bookingForm.email}
                      onChange={handleBookingInputChange}
                      className="w-full rounded-lg border-gray-300 shadow-sm focus:border-green-500 focus:ring-green-500"
                      required
                    />
                  </div>
                  
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">Preferred Date</label>
                      <input
                        type="date"
                        name="date"
                        value={bookingForm.date}
                        onChange={handleBookingInputChange}
                        className="w-full rounded-lg border-gray-300 shadow-sm focus:border-green-500 focus:ring-green-500"
                        required
                      />
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">Preferred Time</label>
                      <input
                        type="time"
                        name="time"
                        value={bookingForm.time}
                        onChange={handleBookingInputChange}
                        className="w-full rounded-lg border-gray-300 shadow-sm focus:border-green-500 focus:ring-green-500"
                        required
                      />
                    </div>
                  </div>
                  
                  <div className="mb-4">
                    <label className="block text-sm font-medium text-gray-700 mb-1">Additional Information</label>
                    <textarea
                      name="message"
                      value={bookingForm.message}
                      onChange={handleBookingInputChange}
                      rows="3"
                      className="w-full rounded-lg border-gray-300 shadow-sm focus:border-green-500 focus:ring-green-500"
                      placeholder="Please describe your condition or any specific requirements"
                    ></textarea>
                  </div>
                  
                  <button
                    type="submit"
                    className="w-full bg-gradient-to-r from-green-600 to-green-500 text-white py-3 px-4 rounded-lg text-lg font-semibold hover:from-green-700 hover:to-green-600 focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-offset-2 transition-all duration-300 transform hover:scale-[1.01] shadow-lg hover:shadow-xl"
                  >
                    <span className="flex items-center justify-center"><FaCalendarAlt className="mr-2" /> Book Appointment</span>
                  </button>
                </form>
              </div>
            </div>
          </div>
        </div>

        {/* Mental Therapy Booking Section */}
        <div className="mb-12">
          <div className="flex items-center mb-6">
            <div className="w-10 h-1 bg-purple-600 rounded-full mr-4"></div>
            <h2 className="text-3xl font-bold text-purple-800">Mental Health Services</h2>
          </div>
          <div className="bg-white p-8 rounded-2xl shadow-xl border-t-4 border-purple-500">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
              <div>
                <div className="text-purple-500 text-5xl mb-4">
                  <FaBrain />
                </div>
                <h3 className="text-2xl font-bold text-gray-800 mb-4">Professional Mental Health Therapy</h3>
                <p className="text-gray-600 mb-4">Our licensed therapists provide confidential and supportive mental health services to help you navigate life's challenges and improve your well-being.</p>
                <ul className="space-y-2 mb-6">
                  <li className="flex items-center"><span className="bg-purple-100 p-1 rounded-full mr-2"></span> Anxiety & Depression Treatment</li>
                  <li className="flex items-center"><span className="bg-purple-100 p-1 rounded-full mr-2"></span> Stress Management</li>
                  <li className="flex items-center"><span className="bg-purple-100 p-1 rounded-full mr-2"></span> Relationship Counseling</li>
                  <li className="flex items-center"><span className="bg-purple-100 p-1 rounded-full mr-2"></span> Trauma Recovery</li>
                </ul>
                <div className="flex items-center text-gray-600 mb-4">
                  <FaPhoneAlt className="mr-2 text-purple-500" />
                  <span>Call us: <span className="font-semibold">+91 9876543211</span></span>
                </div>
              </div>
              <div>
                <form onSubmit={handleBookingSubmit} className="bg-gray-50 p-6 rounded-xl">
                  <h3 className="text-xl font-bold text-gray-800 mb-4">Schedule Your Therapy Session</h3>
                  
                  {bookingSuccess && (
                    <div className="bg-green-100 border border-green-400 text-green-700 px-4 py-3 rounded mb-4">
                      Booking request submitted successfully! We'll contact you shortly.
                    </div>
                  )}
                  
                  {bookingError && (
                    <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
                      {bookingError}
                    </div>
                  )}
                  
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">Your Name</label>
                      <input
                        type="text"
                        name="name"
                        value={bookingForm.name}
                        onChange={handleBookingInputChange}
                        className="w-full rounded-lg border-gray-300 shadow-sm focus:border-purple-500 focus:ring-purple-500"
                        required
                      />
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">Phone Number</label>
                      <input
                        type="tel"
                        name="phone"
                        value={bookingForm.phone}
                        onChange={handleBookingInputChange}
                        className="w-full rounded-lg border-gray-300 shadow-sm focus:border-purple-500 focus:ring-purple-500"
                        required
                      />
                    </div>
                  </div>
                  
                  <div className="mb-4">
                    <label className="block text-sm font-medium text-gray-700 mb-1">Email Address</label>
                    <input
                      type="email"
                      name="email"
                      value={bookingForm.email}
                      onChange={handleBookingInputChange}
                      className="w-full rounded-lg border-gray-300 shadow-sm focus:border-purple-500 focus:ring-purple-500"
                      required
                    />
                  </div>
                  
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">Preferred Date</label>
                      <input
                        type="date"
                        name="date"
                        value={bookingForm.date}
                        onChange={handleBookingInputChange}
                        className="w-full rounded-lg border-gray-300 shadow-sm focus:border-purple-500 focus:ring-purple-500"
                        required
                      />
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">Preferred Time</label>
                      <input
                        type="time"
                        name="time"
                        value={bookingForm.time}
                        onChange={handleBookingInputChange}
                        className="w-full rounded-lg border-gray-300 shadow-sm focus:border-purple-500 focus:ring-purple-500"
                        required
                      />
                    </div>
                  </div>
                  
                  <div className="mb-4">
                    <label className="block text-sm font-medium text-gray-700 mb-1">Type of Therapy</label>
                    <select
                      name="service"
                      value={bookingForm.service}
                      onChange={handleBookingInputChange}
                      className="w-full rounded-lg border-gray-300 shadow-sm focus:border-purple-500 focus:ring-purple-500"
                      required
                    >
                      <option value="">Select a therapy type</option>
                      <option value="anxiety">Anxiety Treatment</option>
                      <option value="depression">Depression Treatment</option>
                      <option value="stress">Stress Management</option>
                      <option value="relationship">Relationship Counseling</option>
                      <option value="trauma">Trauma Recovery</option>
                      <option value="other">Other</option>
                    </select>
                  </div>
                  
                  <div className="mb-4">
                    <label className="block text-sm font-medium text-gray-700 mb-1">Additional Information</label>
                    <textarea
                      name="message"
                      value={bookingForm.message}
                      onChange={handleBookingInputChange}
                      rows="3"
                      className="w-full rounded-lg border-gray-300 shadow-sm focus:border-purple-500 focus:ring-purple-500"
                      placeholder="Please describe your concerns or any specific requirements"
                    ></textarea>
                  </div>
                  
                  <button
                    type="submit"
                    className="w-full bg-gradient-to-r from-purple-600 to-purple-500 text-white py-3 px-4 rounded-lg text-lg font-semibold hover:from-purple-700 hover:to-purple-600 focus:outline-none focus:ring-2 focus:ring-purple-500 focus:ring-offset-2 transition-all duration-300 transform hover:scale-[1.01] shadow-lg hover:shadow-xl"
                  >
                    <span className="flex items-center justify-center"><FaCalendarAlt className="mr-2" /> Schedule Session</span>
                  </button>
                </form>
              </div>
            </div>
          </div>
        </div>

        {/* Blood Collection Section */}
        <div className="mb-12">
          <div className="flex items-center mb-6">
            <div className="w-10 h-1 bg-red-600 rounded-full mr-4"></div>
            <h2 className="text-3xl font-bold text-red-800">Blood Collection & Testing</h2>
          </div>
          <div className="bg-white p-8 rounded-2xl shadow-xl border-t-4 border-red-500">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
              <div>
                <div className="text-red-500 text-5xl mb-4">
                  <FaVial />
                </div>
                <h3 className="text-2xl font-bold text-gray-800 mb-4">Professional Blood Testing Services</h3>
                <p className="text-gray-600 mb-4">Our certified phlebotomists provide safe and efficient blood collection services for a wide range of diagnostic tests. We offer both home collection and lab visits.</p>
                <ul className="space-y-2 mb-6">
                  <li className="flex items-center"><span className="bg-red-100 p-1 rounded-full mr-2"></span> Complete Blood Count (CBC)</li>
                  <li className="flex items-center"><span className="bg-red-100 p-1 rounded-full mr-2"></span> Diabetes Screening</li>
                  <li className="flex items-center"><span className="bg-red-100 p-1 rounded-full mr-2"></span> Lipid Profile</li>
                  <li className="flex items-center"><span className="bg-red-100 p-1 rounded-full mr-2"></span> Thyroid Function Tests</li>
                  <li className="flex items-center"><span className="bg-red-100 p-1 rounded-full mr-2"></span> Liver Function Tests</li>
                </ul>
                <div className="flex items-center text-gray-600 mb-4">
                  <FaPhoneAlt className="mr-2 text-red-500" />
                  <span>Call us: <span className="font-semibold">+91 9876543212</span></span>
                </div>
              </div>
              <div>
                <form onSubmit={handleBookingSubmit} className="bg-gray-50 p-6 rounded-xl">
                  <h3 className="text-xl font-bold text-gray-800 mb-4">Schedule Blood Collection</h3>
                  
                  {bookingSuccess && (
                    <div className="bg-green-100 border border-green-400 text-green-700 px-4 py-3 rounded mb-4">
                      Booking request submitted successfully! We'll contact you shortly.
                    </div>
                  )}
                  
                  {bookingError && (
                    <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
                      {bookingError}
                    </div>
                  )}
                  
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">Your Name</label>
                      <input
                        type="text"
                        name="name"
                        value={bookingForm.name}
                        onChange={handleBookingInputChange}
                        className="w-full rounded-lg border-gray-300 shadow-sm focus:border-red-500 focus:ring-red-500"
                        required
                      />
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">Phone Number</label>
                      <input
                        type="tel"
                        name="phone"
                        value={bookingForm.phone}
                        onChange={handleBookingInputChange}
                        className="w-full rounded-lg border-gray-300 shadow-sm focus:border-red-500 focus:ring-red-500"
                        required
                      />
                    </div>
                  </div>
                  
                  <div className="mb-4">
                    <label className="block text-sm font-medium text-gray-700 mb-1">Email Address</label>
                    <input
                      type="email"
                      name="email"
                      value={bookingForm.email}
                      onChange={handleBookingInputChange}
                      className="w-full rounded-lg border-gray-300 shadow-sm focus:border-red-500 focus:ring-red-500"
                      required
                    />
                  </div>
                  
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">Preferred Date</label>
                      <input
                        type="date"
                        name="date"
                        value={bookingForm.date}
                        onChange={handleBookingInputChange}
                        className="w-full rounded-lg border-gray-300 shadow-sm focus:border-red-500 focus:ring-red-500"
                        required
                      />
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">Preferred Time</label>
                      <input
                        type="time"
                        name="time"
                        value={bookingForm.time}
                        onChange={handleBookingInputChange}
                        className="w-full rounded-lg border-gray-300 shadow-sm focus:border-red-500 focus:ring-red-500"
                        required
                      />
                    </div>
                  </div>
                  
                  <div className="mb-4">
                    <label className="block text-sm font-medium text-gray-700 mb-1">Test Type</label>
                    <select
                      name="service"
                      value={bookingForm.service}
                      onChange={handleBookingInputChange}
                      className="w-full rounded-lg border-gray-300 shadow-sm focus:border-red-500 focus:ring-red-500"
                      required
                    >
                      <option value="">Select a test type</option>
                      <option value="cbc">Complete Blood Count (CBC)</option>
                      <option value="diabetes">Diabetes Screening</option>
                      <option value="lipid">Lipid Profile</option>
                      <option value="thyroid">Thyroid Function Tests</option>
                      <option value="liver">Liver Function Tests</option>
                      <option value="other">Other</option>
                    </select>
                  </div>
                  
                  <div className="mb-4">
                    <label className="block text-sm font-medium text-gray-700 mb-1">Collection Location</label>
                    <div className="flex items-center space-x-4">
                      <label className="inline-flex items-center">
                        <input type="radio" name="location" value="home" className="text-red-600 focus:ring-red-500" />
                        <span className="ml-2">Home Collection</span>
                      </label>
                      <label className="inline-flex items-center">
                        <input type="radio" name="location" value="lab" className="text-red-600 focus:ring-red-500" />
                        <span className="ml-2">Visit Lab</span>
                      </label>
                    </div>
                  </div>
                  
                  <div className="mb-4">
                    <label className="block text-sm font-medium text-gray-700 mb-1">Additional Information</label>
                    <textarea
                      name="message"
                      value={bookingForm.message}
                      onChange={handleBookingInputChange}
                      rows="3"
                      className="w-full rounded-lg border-gray-300 shadow-sm focus:border-red-500 focus:ring-red-500"
                      placeholder="Please provide your address for home collection or any specific requirements"
                    ></textarea>
                  </div>
                  
                  <button
                    type="submit"
                    className="w-full bg-gradient-to-r from-red-600 to-red-500 text-white py-3 px-4 rounded-lg text-lg font-semibold hover:from-red-700 hover:to-red-600 focus:outline-none focus:ring-2 focus:ring-red-500 focus:ring-offset-2 transition-all duration-300 transform hover:scale-[1.01] shadow-lg hover:shadow-xl"
                  >
                    <span className="flex items-center justify-center"><FaCalendarAlt className="mr-2" /> Schedule Collection</span>
                  </button>
                </form>
              </div>
            </div>
          </div>
        </div>

        {/* Search Form */}
        <form onSubmit={handleSearch} className="bg-white p-8 rounded-2xl shadow-2xl mb-12 transform transition-all duration-300 hover:shadow-2xl border border-blue-100 backdrop-filter backdrop-blur-sm bg-opacity-90">
          <div className="flex items-center mb-6">
            <div className="w-8 h-1 bg-blue-600 rounded-full mr-4"></div>
            <h2 className="text-2xl font-bold text-blue-800">Find Your Doctor</h2>
          </div>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div className="relative group">
              <label className="block text-sm font-medium text-gray-700 mb-1">Symptoms</label>
              <span className="absolute left-3 top-9 text-blue-500 group-hover:text-blue-700 transition-colors"><FaSearch /></span>
              <input
                type="text"
                name="symptoms"
                value={searchParams.symptoms}
                onChange={handleInputChange}
                className="pl-10 mt-1 block w-full rounded-lg border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500 transition-all duration-300 hover:border-blue-300"
                placeholder="e.g. fever, cough"
              />
            </div>
            <div className="relative group">
              <label className="block text-sm font-medium text-gray-700 mb-1">Location</label>
              <span className="absolute left-3 top-9 text-blue-500 group-hover:text-blue-700 transition-colors"><FaMapMarkerAlt /></span>
              <input
                type="text"
                name="location"
                value={searchParams.location}
                onChange={handleInputChange}
                className="pl-10 mt-1 block w-full rounded-lg border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500 transition-all duration-300 hover:border-blue-300"
                placeholder="City or State"
              />
            </div>
            <div className="relative group">
              <label className="block text-sm font-medium text-gray-700 mb-1">Specialty</label>
              <span className="absolute left-3 top-9 text-blue-500 group-hover:text-blue-700 transition-colors"><FaStethoscope /></span>
              <input
                type="text"
                name="specialty"
                value={searchParams.specialty}
                onChange={handleInputChange}
                className="pl-10 mt-1 block w-full rounded-lg border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500 transition-all duration-300 hover:border-blue-300"
                placeholder="e.g. Cardiologist"
              />
            </div>
            <div className="relative group">
              <label className="block text-sm font-medium text-gray-700 mb-1">Doctor Name</label>
              <span className="absolute left-3 top-9 text-blue-500 group-hover:text-blue-700 transition-colors"><FaUser /></span>
              <input
                type="text"
                name="doctorName"
                value={searchParams.doctorName}
                onChange={handleInputChange}
                className="pl-10 mt-1 block w-full rounded-lg border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500 transition-all duration-300 hover:border-blue-300"
                placeholder="e.g. Sujay Kumar Das"
              />
            </div>
          </div>
          {/* Suggestions dropdown */}
          {showSuggestions && suggestions.length > 0 && (
            <div className="bg-white border rounded-lg shadow-lg mt-2 max-h-60 overflow-y-auto absolute z-50 w-full md:w-auto md:min-w-[300px] animate-fadeIn">
              {suggestions.map((s, idx) => (
                <div
                  key={s._id || idx}
                  className="px-4 py-2 hover:bg-blue-50 cursor-pointer transition-colors duration-200"
                  onClick={() => handleSuggestionClick(s)}
                >
                  <div className="font-semibold text-blue-700">{s.name}</div>
                  <div className="text-xs text-gray-500">{s.specialty} | {s.city} {s.state} {s.hospitalName}</div>
                </div>
              ))}
            </div>
          )}
          <div className="mt-8">
            <button
              type="submit"
              disabled={loading}
              className="w-full bg-gradient-to-r from-blue-700 to-blue-500 text-white py-4 px-6 rounded-lg text-lg font-semibold hover:from-blue-800 hover:to-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 disabled:opacity-50 transition-all duration-300 transform hover:scale-[1.01] shadow-lg hover:shadow-xl"
            >
              {loading ? (
                <span className="flex items-center justify-center"><svg className="animate-spin h-5 w-5 mr-2 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24"><circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle><path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8v8z"></path></svg>Searching...</span>
              ) : (
                <span className="flex items-center justify-center"><FaSearch className="mr-2" /> Search Doctors</span>
              )}
            </button>
          </div>
        </form>

        {error && (
          <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
            {error}
          </div>
        )}

        {/* Doctor Results */}
        {doctors.length > 0 && (
          <div>
            <div className="flex items-center mb-6">
              <div className="w-10 h-1 bg-blue-600 rounded-full mr-4"></div>
              <h2 className="text-3xl font-bold text-blue-800">Search Results</h2>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
              {doctors.map(doctor => (
                <div key={doctor._id} className="bg-white p-6 rounded-2xl shadow-xl hover:shadow-2xl transition border-t-4 border-blue-500 flex flex-col justify-between h-full transform hover:-translate-y-1 duration-300">
                  <div>
                    <h2 className="text-2xl font-bold mb-1 text-blue-700 flex items-center gap-2"><FaStethoscope className="inline-block" /> {doctor.name}</h2>
                    <span className="inline-block bg-blue-100 text-blue-700 text-xs px-2 py-1 rounded-full mb-2">{doctor.specialty}</span>
                    <p className="text-gray-600 mb-2 font-medium">{doctor.hospitalName}</p>
                    <p className="text-gray-500 mb-2 flex items-center"><FaMapMarkerAlt className="mr-1" /> {doctor.address}, {doctor.city}, {doctor.state}</p>
                    <p className="text-gray-500 mb-2">Phone: <span className="font-semibold">{doctor.phone}</span></p>
                    <p className="text-gray-500 mb-2">Fees: <span className="font-semibold">{doctor.fees}</span></p>
                    <p className="text-gray-500 mb-2">Availability: <span className="font-semibold">{doctor.availability}</span></p>
                  </div>
                  <div className="mt-4 flex flex-wrap gap-2">
                    {doctor.symptomsTreated && doctor.symptomsTreated.map((sym, idx) => (
                      <span key={idx} className="bg-gray-200 text-gray-700 text-xs px-2 py-1 rounded-full">{sym}</span>
                    ))}
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Home;
